entry = ""

entry_list_format = [list for list in entry]

elements_to_evalue = entry_list_format
elements_deleted = 0
marcador_de_inicio_paquete = 4

#Respuesta del primer ejercicio
for evaluar_paquete in entry_list_format:
    elements_to_evalue = elements_to_evalue
    if len(set(elements_to_evalue[:marcador_de_inicio_paquete])) == marcador_de_inicio_paquete:
        print(f"la posición del marcador de paquete es {elements_deleted+len(set(elements_to_evalue[:marcador_de_inicio_paquete]))}") 
        break
    else:
        elements_to_evalue.pop(0)
        elements_deleted = elements_deleted + 1

